  { id: 'citra', name: 'Citra', version: 'Nightly 2048', status: 'updating', core: '3DS', icon: '' },
];

const MOCK_GAMES: Game[] = [
  { id: '1', title: 'The Legend of Zelda: TOTK', platform: 'Switch', coverUrl: 'https://picsum.photos/300/400?random=1', playTimeHrs: 120, lastPlayed: '2 hours ago', emulatorId: 'yuzu', favorite: true },
  { id: '2', title: 'Metroid Prime Remastered', platform: 'Switch', coverUrl: 'https://picsum.photos/300/400?random=2', playTimeHrs: 15, lastPlayed: 'Yesterday', emulatorId: 'yuzu', favorite: false },
  { id: '3', title: 'Persona 5 Royal', platform: 'Switch', coverUrl: 'https://picsum.photos/300/400?random=3', playTimeHrs: 85, lastPlayed: '3 days ago', emulatorId: 'ryujinx', favorite: true },
  { id: '4', title: 'God of War II', platform: 'PS2', coverUrl: 'https://picsum.photos/300/400?random=4', playTimeHrs: 40, lastPlayed: 'Last week', emulatorId: 'pcsx2', favorite: true },
  { id: '5', title: 'Metal Gear Solid 3', platform: 'PS2', coverUrl: 'https://picsum.photos/300/400?random=5', playTimeHrs: 25, lastPlayed: '2 weeks ago', emulatorId: 'pcsx2', favorite: false },
  { id: '6', title: 'Super Mario Galaxy 2', platform: 'Wii', coverUrl: 'https://picsum.photos/300/400?random=6', playTimeHrs: 30, lastPlayed: 'A month ago', emulatorId: 'dolphin', favorite: false },
  { id: '7', title: 'Demon\'s Souls', platform: 'PS3', coverUrl: 'https://picsum.photos/300/400?random=7', playTimeHrs: 60, lastPlayed: 'Yesterday', emulatorId: 'rpcs3', favorite: true },
  { id: '8', title: 'Pokemon X', platform: '3DS', coverUrl: 'https://picsum.photos/300/400?random=8', playTimeHrs: 45, lastPlayed: 'Today', emulatorId: 'citra', favorite: false },
];

export default function App() {
  const [currentView, setCurrentView] = useState<ViewState>(ViewState.DASHBOARD);
  const [emulators, setEmulators] = useState<Emulator[]>(MOCK_EMULATORS);
  const [stats, setStats] = useState<SystemStats>({
    cpuUsage: 15,
    gpuUsage: 5,
    ramUsage: 8.4,
    fps: 144,
    temperature: 42
  });

  // Mock live stats update
  useEffect(() => {
    const interval = setInterval(() => {
      setStats(prev => ({
        ...prev,
        cpuUsage: Math.floor(Math.random() * 20) + 10,
        gpuUsage: Math.floor(Math.random() * 30) + 20,
        ramUsage: Number((Math.random() * 2 + 8).toFixed(1)),
        temperature: Math.floor(Math.random() * 5) + 40,
      }));
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  const handleToggleEmulator = (id: string) => {
    setEmulators(prev => prev.map(emu => {
      if (emu.id === id) {
        return { ...emu, status: emu.status === 'running' ? 'stopped' : 'running' };
      }
      return emu;
    }));
  };

  const handleLaunchGame = (id: string) => {
    console.log(`Launching game ${id}...`);
    // In a real app, this would trigger an IPC call to the backend
  };

  return (
    <Layout currentView={currentView} onNavigate={setCurrentView}>
      {currentView === ViewState.DASHBOARD && (
        <EmulatorDashboard 
          stats={stats} 
          emulators={emulators} 
          onToggleEmulator={handleToggleEmulator} 
        />
      )}

      {currentView === ViewState.LIBRARY && (
        <GameLibrary 
          games={MOCK_GAMES} 
          onLaunchGame={handleLaunchGame} 
        />
      )}

      {(currentView === ViewState.EMULATORS || currentView === ViewState.SETTINGS) && (
        <div className="flex items-center justify-center h-[50vh] flex-col gap-4 text-muted-foreground">
          <div className="w-16 h-16 rounded-full bg-accent/50 flex items-center justify-center">
            <span className="text-2xl">ðŸš§</span>
          </div>
          <h2 className="text-xl font-semibold text-foreground">Work in Progress</h2>
          <p>This module is currently being built by the engineering team.</p>
          <Button variant="outline" onClick={() => setCurrentView(ViewState.DASHBOARD)}>Return Home</Button>
        </div>
      )}
    </Layout>
  );
}

